import React from "react";


class PeopleApi extends React.Component {
    state = {
        personImage: []
    }
    persons = async () => {
        console.log(this.state.personImage);
        let random = 'https://randomuser.me/api/?results=10'
        let info = await (await fetch(random)).json();
        for (let i = 0; i <= 9; i++) {
            // console.log(info.results[i].picture.large);
            this.setState({ personImage: info.results[i].picture.large })
        }
        console.log(this.state.personImage);
    }



    render() {
        return (
            <div>
                <input type="button" value="click" onClick={this.persons} /><br /><br />
                <img src={this.state.personImage}></img>
            </div>
        )

    }

}

export default PeopleApi
