import React from "react";
import './style.css'

class HideAndSeek extends React.Component {
    constructor(props) {
        super(props)
        // this.state = {
        //     display: condition
        // }
    }
    ShowOrHide = () => {
        if (condition == 'none') {
            condition == 'block'
        }
        else{
            condition == 'none'
        }

    }
    render() {
        return (
            <div>
                <input type='button' value='show/hide' onClick={this.ShowOrHide} />
                <div className='yellow' style={{ display: { condition } }}></div>
            </div>
        )
    }
}

export default HideAndSeek
