import React from "react";


class PeopleApi extends React.Component {
    state = {
        personImage: ''
    }
    person = async () => {
        // try {
        let random = 'https://randomuser.me/api/'
        let info = await (await fetch(random)).json();
        console.log(info.picture.large);
        this.setState({ personImage: info.picture.large })
        // } catch (err) {
        // this.setState({ personImage: 'No image sadly' })
        // }

    }

    render() {
        return (
            <div>
                <input type="button" value="click" onClick={this.person} />
                {this.state.personImage}
            </div>
        )

    }

}

export default PeopleApi
