import React from "react";
import './style.css'


class PeopleApi extends React.Component {
    state = {
        personImage: [],
        personName: []
    }
    componentDidMount = async () => {
        let random = 'https://randomuser.me/api/?results=10'
        let info = await (await fetch(random)).json();
        for (let i = 0; i <= 9; i++) {
            console.log(info.results[i]);
            // console.log(info.results[i].picture.large);
            this.setState({ personImage: [...this.state.personImage, info.results[i].picture.large] })
            console.log(info.results[i].name.first);
            this.setState({ personName: [...this.state.personName, info.results[i].name.first] })
        }
        // console.log(this.state.personImage);
        console.log(this.state.personName);
    }

    person10 = () => {
        let arr = []
        for (let i = 0; i <= 9; i++) {
            arr.push(<><span><img src={this.state.personImage[i]}>{this.state.personName[i]}</img></span></>)
        }
        return arr
    }

    render() {

        return (
            <div>
                <div className="container">
                {this.person10()}
                </div>
            </div>
        )

    }

}

export default PeopleApi
