import React from "react";


class PeopleApi extends React.Component {
    state = {
        personImage: ''
    }
    async componentDidMount() {

        let random = 'https://randomuser.me/api/'
        let info = await (await fetch(random)).json();
        for (let i = 0; i <= 10; i++) {
            console.log(info.results[i]);
            // this.setState({ personImage: info.results[0].picture.large })
        }
    }



    render() {
        return (
            <div>
                {/* <input type="button" value="click" onClick={this.person} /><br /><br /> */}
                <img src={this.state.personImage}></img>
            </div>
        )

    }

}

export default PeopleApi
