import React from "react";
import './style.css'
class Spinner extends React.Component {

    state = { renderContent: '',timer:5 }
    spinner1() {
        return (
            <>
        <div className="loader">
            </div>
       
            </>
        )
    }
    spinner2() {
        return <div className="loader">Loading...</div>
    }
    spinner3() {
        return <div className="loader">Loading...</div>
    }
    getRandomSpinner = () => {
        let rand = Math.floor((Math.random() * 1) + 1)
        if (rand == 1)
            return this.spinner1()
        if (rand == 2)
            return this.spinner2()
        if (rand == 3)
            return this.spinner3()
    }

    componentDidMount() {
        this.setState({ renderContent: this.getRandomSpinner()})
        setInterval(() => {
            this.setState({ timer:--this.state.timer })
        }, 1000);
   
    }

    componentDidUpdate() {
        setTimeout(() => {
   
            this.setState({ renderContent: '' ,timer:0})
        }, 5000);
    }

    render() {

        return (
            <div>
                {this.state.renderContent}
               <div className="timer"> {this.state.timer}</div>
            </div>
        )

    }
}

export default Spinner
