import React from "react";


const data = [
    {
        name: "John",
        birthday: "1-1-1995",
        favoriteFoods: {
            meats: ["hamburgers", "sausages"],
            fish: ["salmon", "pike"],
        },
    },
    {
        name: "Mark",
        birthday: "10-5-1980",
        favoriteFoods: {
            meats: ["hamburgers", "steak", "lamb"],
            fish: ["tuna", "salmon", "barracuda"],
        },
    },
    {
        name: "Mary",
        birthday: "1-10-1977",
        favoriteFoods: {
            meats: ["ham", "chicken"],
            fish: ["pike"],
        },
    },
    {
        name: "Thomas",
        birthday: "1-10-1990",
        favoriteFoods: {
            meats: ["bird", "rooster"],
            fish: ["salmon"],
        },
    },
    {
        name: "Johnny",
        birthday: "1-10-1992",
        favoriteFoods: {
            meats: ["hamburgers", "lamb"],
            fish: ["anchovies", "tuna"],
        },
    },
];

class Data extends React.Component {
    state = { people: '' }
    componentDidMount() {
        const allNames = data.map(filteredPerson => (
            <li>
                {filteredPerson.name}
            </li>
        ))
        const propleOlder1990 = data.forEach(born => {
            let before1990 = born.birthday.replace(/\-/g, ' ').split(' ')[2]
            if (before1990 < 1990) {
                this.setState({ people: born });
            }
        })
    }
    render() {
        return (
            <div>
                {this.allNames}
                {this.propleOlder1990}
            </div>
        )
    }
}

export default Data
