import React from "react";
import { useEffect } from "react";

const StarWarsApi = () => {
    const [link, setLink] = React.useState('1/')

    useEffect(async () => {
        let random = `https://swapi.dev/api/films/${link}`
        let info = await (await fetch(random)).json();
        return (
            <div>
                <>{console.log(info)}</>
            </div>
        )
    })
}

export default StarWarsApi
