import React from "react";


class GetJokes extends React.Component {
    state = { respons: null }

    async joke() {
        let random = 'https://api.chucknorris.io/jokes/random'
        let info = ((await (await fetch(random)).json())).value;
        console.log(info);
        console.log(5);
    }
  async  render() {
        return (
            <div>
                {this.joke()}
            </div>
        )

    }
}

export default GetJokes
