import React from "react";


class GetJokes extends React.Component {
    constructor() {
        super()
        this.state = {
            afterOneSec: false,
            displayJoke: 'hhh'
        }
    }
    


    joke() {
        // let random = 'https://api.chucknorris.io/jokes/random'
        // let info = ((await (await fetch(random)).json())).value;
        // document.querySelector('.jokeIsHere').innerHTML = info
        this.setState({
            afterOneSec: !this.state.afterOneSec
        })
        // this.setState({ displayJoke: 'green' })
    }
    render() {
        return (
            <div>
                <input type="button" value="click" onClick={this.joke} />
                <div className="jokeIsHere"></div>
                {this.state.displayJoke}
            </div>
        )

    }
}

export default GetJokes
