import React from "react";
import './style.css'


class Child extends React.Component {
    state = {
        blueBtn: 'blue',
        redBtn: 'red',
        yellowBtn: 'yellow'
    }
    onRedBtnClickEvent = (event) => {
        this.props.onClick(this.state.blueBtn)
        // console.log(this.state.blueBtn);
    }

    render() {
        return (
            <div>
                <input type="button" value="blue" style={{ backgroundColor: 'blue' }} onClick={this.onRedBtnClickEvent}></input><br />
                <input type="button" value="red" style={{ backgroundColor: 'red' }}></input><br />
                <input type="button" value="yellow" style={{ backgroundColor: 'yellow' }}></input>
            </div>
        )
    }

}
export default Child
