import React from "react";
import './style.css'
class Spinner extends React.Component {
    constructor() {
        super()
    }
    spinner1() {
        return <div className="loader">Loading...</div>
    }
    componentDidMount() {
        return (
            setTimeout(() => {
                { this.spinner1() }
            }, 10000)
        )
    }
    render() {
        return (
            <div>
                {this.componentDidMount()}
            </div>
        )

    }
}

export default Spinner
