import { useEffect } from "react";

const CountriesApi = () => {
    const [country, setCountry] = React.useState([])

    useEffect(async () => {
        let random = `https://restcountries.com/v2/all`
        let info = (await (await fetch(random)).json());
        setCountry(info.name)
        console.log(country);
    }, [])
    return (
        <div>
            {/* {country.map((count) => {
