import React from "react";
import './style.css'

class BoxAnimation extends React.Component {
    constructor() {
        super()
    }

    componentDidMount() {
        setTimeout(() => {
            document.querySelector(".test").animate([
                // keyframes
                { transform: 'translatex(-100px)' },
                { transform: 'translatex(0px)' }
            ], {
                // timing options
                duration: 5000,
                iterations: 1
            });
        }, 4000);

    }
    render() {
        return (
            <div>
                <div className='test'>
                    hello
                </div>


            </div>
        )
    }
}

export default BoxAnimation
