import React from "react";
import './style.css'

class HideAndSeek extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
           display: 'none'
        }
    }
    ShowOrHide = () => {
        this.setState(prevState => ({
            isAppearing: !prevState.isAppearing
        }))
    }
    render() {
        return (
            <div>
                <input type='button' value='show/hide' onClick={this.ShowOrHide} />
                <div className='yellow' style={{display: this.state.display ? 'none' : 'block'}}></div>
            </div>
        )
    }
}

export default HideAndSeek
