import React from "react";

class BoxAnimation extends React.Component {
    constructor() {
        super()
    }
    
    componentDidMount() {
        document.querySelector(".test").animate([
            // keyframes
            { transform: 'translatex(0px)' },
            { transform: 'translatex(+300px)' }
          ], {
            // timing options
            duration: 5000,
            iterations: Infinity
          });
    }
    render() {
        return (
            <div>
                <div className='test'>
                        hello
                </div>


            </div>
        )
    }
}

export default BoxAnimation
