import React from "react"
import './style.css'
const Todo = () => {
    const [check, setCheck] = React.useState
        ([
            { name: "CSS", completed: true },
            { name: "JavaScript", completed: true },
            { name: "Learn React", completed: false },
            { name: "Learn mongoDB", completed: false },
            { name: "Learn Node JS", completed: false },
        ])
    const Clicked = (e) => {
        let newCheck = { ...check }
        console.log(e.target.id);
        // newCheck[e.target.id]
        setCheck([
            { name: "CSS", completed: true },
            { name: "JavaScript", completed: true },
            { name: "Learn React", completed: false },
            { name: "Learn mongoDB", completed: false },
            { name: "Learn Node JS", completed: false },
        ])

    }
    return (
        <div className='container' style={{ textAlign: 'center' }}>
            <p className={check[0].completed ? 'lineThrough' : 'none'}>{check[0].name}<input id={0} type='button' value={check[0].completed ? '✓' : 'X'} onClick={Clicked} /></p>
            <p className={check[1].completed ? 'lineThrough' : 'none'}>{check[1].name}<input id={1} type='button' value={check[1].completed ? '✓' : 'X'} onClick={Clicked} /></p>
            <p className={check[2].completed ? 'lineThrough' : 'none'}>{check[2].name}<input id={2} type='button' value={check[2].completed ? '✓' : 'X'} onClick={Clicked} /></p>
            <p className={check[3].completed ? 'lineThrough' : 'none'}>{check[3].name}<input id={3} type='button' value={check[3].completed ? '✓' : 'X'} onClick={Clicked} /></p>
            <p className={check[4].completed ? 'lineThrough' : 'none'}>{check[4].name}<input id={4} type='button' value={check[4].completed ? '✓' : 'X'} onClick={Clicked} /></p>
        </div>
    )
}
export default Todo
