import React from "react";
import { useEffect } from "react";

const CountriesApi = () => {
    const [country, setCountry] = React.useState([])

    useEffect(async () => {
        let random = `https://restcountries.com/v2/all`
        let info = (await (await fetch(random)).json());
        let AllCountriesName = info.map(count => {
            return count.name
        })
        setCountry(AllCountriesName)


    }, [])
    return (
        <div>

            <input id='searchField' type='text' placeholder='Country Search...' onChange={(e) => { this.props.returnFunction(e.target.value) }} />

            {country.map((count, idx) => {
                return <ul>
                    <li key={idx}> {count}</li>
                </ul>
            })}
        </div>
    )
}

export default CountriesApi
