import React from "react";
import './style.css'
import Input from "./input";


class PeopleApi extends React.Component {
    state = {
        persons: [],
        bySearch: []
    }
    componentDidMount = async () => {
        let random = 'https://randomuser.me/api/?results=10'
        let info = await (await fetch(random)).json();
        for (let i = 0; i <= 9; i++) {
            this.setState({
                persons: [...this.state.persons, {
                    picture: info.results[i].picture.large,
                    name: info.results[i].name.first
                }]
            })
        }
    }
    callbackFunction = (e) => {
        let bySearchPeople = this.state.persons.filter(name => {
            return name.toLowerCase().includes(e.target.value.toLowerCase())
        })
        this.setState({ bySearch: bySearchPeople })
    }



    render() {

        return (
            <div>
                <span className='filter'>Filter: </span>
                <Input callbackFunction={callbackFunction} />

                <div className='grid'>
                    {this.state.bySearch.map(people => {
                        return <div className='dirDown'>
                            {people.name}
                            {<img src={people.picture}></img>}
                        </div>
                    })}

                </div>

            </div>
        )

    }
}



export default PeopleApi
