import React from "react";
import './style.css'
import Input from "./input";


class PeopleApi extends React.Component {
    state = {
        personImage: [],
        personName: []
    }
    componentDidMount = async () => {
        let random = 'https://randomuser.me/api/?results=10'
        let info = await (await fetch(random)).json();
        for (let i = 0; i <= 9; i++) {
            this.setState({ personImage: [...this.state.personImage, info.results[i].picture.large] })
            this.setState({ personName: [...this.state.personName, info.results[i].name.first] })
        }
    }
    callbackFunction = (returnedValue) => {
        console.log(this.state.personName.includes(returnedValue));

    }
    person10 = () => {
        let arr = []
        for (let i = 0; i <= 9; i++) {
            arr.push(<><div className="PictureAndName"><span>{this.state.personName[i]}</span><img src={this.state.personImage[i]} /></div></>)
        }
        return arr
    }

    render() {

        return (
            <div>
                <Input callbackFunction={this.callbackFunction} />
                <div className='flex'>
                    {this.person10()}
                </div>

            </div>
        )

    }

}

export default PeopleApi
