    }
    render() {
        return (
            <div className="card">
                {/* <img alt="image" src={this.image} /> */}
                {/* <p>{this.title}</p> */}
                <p>{this.description}</p>
                <a href="/">SHARE</a>
                <a href="/">EXPLORE</a>
            </div>
        )

    }
}
