import React from "react";
import './style.css'
import Child from "./child"

class Parent extends React.Component {
    onRedBtnClickEvent(term) {
        console.log(term);
    }

    render() {
        return (
            <div>
                <Child onRedClick={this.onRedBtnClickEvent} /><br /><br /><br />
                The color selected is:
            </div>
        )
    }

}

export default Parent
