        // } else {

        //     let AllCountriesNameBySearch = info.map(countBySearch => {
        //         return countBySearch.name
        //     })
        //     setSearch(AllCountriesNameBySearch)
        // }
    }
    return (
        <div>

            <input type='text' placeholder='Country Search...' onChange={(e) => Changed(e)} />

            {country.map((count, idx) => {
                return <ul>
                    <li key={idx}> {count}</li>
                </ul>
            })}
        </div>
    )
}

export default CountriesApi
