import React from "react";
import './style.css'
class Spinner extends React.Component {

    state={renderContent:''}
    spinner1() {
        
        return <div className="loader">Loading...</div>
    }
    componentDidMount() {
        return (
            setTimeout(() => {
              this.setState({renderContent:this.spinner1()})
            }, 5000)
        )
    }
    render() {
        return (
            <div>
                {this.state.renderContent}
            </div>
        )

    }
}

export default Spinner
