import React from "react";

class LifeCycleMethods extends React.Component {
    constructor() {
        super()
        this.state = {
            favoriteColor: 'red'
        }
    }
    componentDidMount() {
        setTimeout(() => {
            <h1>My favorite color is {this.state.favoriteColor == 'green'}</h1>
        }, 1000);
    }
    render() {
        return (
            <div>
                <h1>My favorite color is {this.state.favoriteColor}</h1>
                <div id=""></div>
            </div>
        )
    }
}

export default LifeCycleMethods
